import "./styles.css";
import { Supply, Connection, clusterApiUrl } from "@solana/web3.js";
import { useEffect, useState } from "react";
import { displayTimestampUtc } from "./utils/date";
import {
  Table,
  Thead,
  Tbody,
  Tfoot,
  Tr,
  Th,
  Td,
  TableCaption,
} from "@chakra-ui/react";

export const SAMPLE_HISTORY_HOURS = 6;

export type ClusterStats = {
  absoluteSlot: number;
  blockHeight: number | undefined;
  blockTime: number;
  currentEpoch: string;
  epochProgress: string;
};

export default function StatsCard() {
  const [clusterStats, setClusterStats] = useState<ClusterStats>();
  useEffect(() => {});
  async function getClusterStats() {
    console.log("Hello");
    const url = clusterApiUrl("devnet").replace("api", "explorer-api");
    const connection = new Connection(url);
    const samples = await connection.getRecentPerformanceSamples(
      60 * SAMPLE_HISTORY_HOURS
    );
    const transactionCount = await connection.getTransactionCount();
    const epochInfo = await connection.getEpochInfo();
    const blockTime = await connection.getBlockTime(epochInfo.absoluteSlot);
    const { blockHeight, absoluteSlot } = epochInfo;
    const currentEpoch = epochInfo.epoch.toString();
    const { slotIndex, slotsInEpoch } = epochInfo;
    const epochProgress = ((100 * slotIndex) / slotsInEpoch).toFixed(1) + "%";

    // console.log(samples);
    if (blockTime !== null) {
      const clusterStatsData = {
        absoluteSlot,
        blockHeight,
        blockTime: blockTime * 1000,
        currentEpoch,
        epochProgress,
      };
      setClusterStats(clusterStatsData);
    }
  }
  return (
    <div className="App">
      <h1>Stats Card</h1>
      <button onClick={() => getClusterStats()}>Supply</button>
      {clusterStats ? (
        <>
          <Table variant="simple">
            <TableCaption>Imperial to metric conversion factors</TableCaption>
            <Thead>
              <Tr>
                <Th>Live Cluster Stats</Th>
              </Tr>
            </Thead>
            <Tbody>
              <Tr>
                <Td>Slot</Td>
                <Td>millimetres (mm)</Td>
              </Tr>
              <Tr>
                <Td>feet</Td>
                <Td>centimetres (cm)</Td>
                <Td isNumeric>30.48</Td>
              </Tr>
              <Tr>
                <Td>yards</Td>
                <Td>metres (m)</Td>
                <Td isNumeric>0.91444</Td>
              </Tr>
            </Tbody>
            <Tfoot>
              <Tr>
                <Th>To convert</Th>
                <Th>into</Th>
                <Th isNumeric>multiply by</Th>
              </Tr>
            </Tfoot>
          </Table>
          <tr>
            <td className="w-100">Slot</td>
            <td className="text-lg-right text-monospace">
              {clusterStats.absoluteSlot}
            </td>
          </tr>
          {clusterStats.blockHeight !== undefined && (
            <tr>
              <td className="w-100">Block height</td>
              <td className="text-lg-right text-monospace">
                {clusterStats.blockHeight}
              </td>
            </tr>
          )}
          {clusterStats.blockTime && (
            <tr>
              <td className="w-100">Cluster time</td>
              <td className="text-lg-right text-monospace">
                {displayTimestampUtc(clusterStats.blockTime)}
              </td>
            </tr>
          )}
          <tr>
            <td className="w-100">Epoch</td>
            <td className="text-lg-right text-monospace">
              {clusterStats.currentEpoch}
            </td>
          </tr>
          <tr>
            <td className="w-100">Epoch progress</td>
            <td className="text-lg-right text-monospace">
              {clusterStats.epochProgress}
            </td>
          </tr>
        </>
      ) : (
        <p>No Data</p>
      )}
    </div>
  );
}
