import { PublicKey } from "@solana/web3.js";
import { Connection, clusterApiUrl } from "@solana/web3.js";
import { useEffect, useState } from "react";

import { useAtom } from "jotai";
import { queryAtom } from "../state/searchValue";
import HistoryCard from "./HistoryCard";
import { Heading, VStack } from "@chakra-ui/react";
export type DataDetails = {
  program: string;
  parsed: {
    info: {
      decimals?: number;
      freezeAuthority?: string;
      isInitialized?: boolean;
      mintAuthority?: string;
      supply?: string;
      isNative?: false;
      mint?: string;
      owner?: string;
      state?: string;
      tokenAmount?: {
        amount: string;
        decimals: number;
        uiAmount: number;
        uiAmountString: string;
      };
    };
    type: string;
  };
};

export type Data = {
  pubkey: PublicKey;
  lamports: number;
  details?: {
    space: number;
    executable: boolean;
    owner: PublicKey;
    data?: DataDetails;
  };
};

export function AccountHeader({ data }: { data?: Data }) {
  // const detatailsData = data?.details?.data;
  const [detailsData, setDetailsData] = useState<DataDetails>();

  useEffect(() => {
    setDetailsData(data?.details?.data);
    console.log("detatailsData", detailsData);
  }, [data, detailsData]);
  const isToken =
    detailsData?.program === "spl-token" && detailsData?.parsed.type === "mint";

  if (isToken) {
    return (
      <VStack
        my="2"
        w="100%"
        align="start"
        background="white"
        rounded="lg"
        p="4"
      >
        <Heading size="md">Token Account</Heading>
        <tr>
          <td>Address</td>
          <td className="text-lg-right">{data?.pubkey.toBase58()}</td>
        </tr>
        <tr>
          <td>Current Supply</td>
          <td className="text-lg-right">{detailsData?.parsed.info.supply}</td>
        </tr>
        <tr>
          <td>Mint Authority</td>
          <td className="text-lg-right">
            {detailsData?.parsed.info.mintAuthority}
          </td>
        </tr>
        <tr>
          <td>Decimals</td>
          <td className="text-lg-right">{detailsData?.parsed.info.decimals}</td>
        </tr>
      </VStack>
    );
  }

  return (
    <>
      <h6 className="header-pretitle">Details</h6>
      <h2 className="header-title">Account</h2>
      <tr>
        <td>Address</td>
        <td className="text-lg-right">{data?.pubkey.toBase58()}</td>
      </tr>
      <tr>
        <td>Mint</td>
        <td className="text-lg-right">{detailsData?.parsed.info.mint}</td>
      </tr>
      <tr>
        <td>Owner</td>
        <td className="text-lg-right">{detailsData?.parsed.info.owner}</td>
      </tr>
      <tr>
        <td>State</td>
        <td className="text-lg-right">{detailsData?.parsed.info.state}</td>
      </tr>
    </>
  );
}

export default function AccountDetailsCard() {
  const [query] = useAtom(queryAtom);
  const [data, setData] = useState<Data>();

  // const fetchAccount = useFetchAccountInfo();
  // const { status } = useCluster();
  // const info = useAccountInfo(address);
  let pubkey: PublicKey | undefined;

  // Fetch account on load
  useEffect(() => {}, [query.searchValue]);

  // let data;
  async function Hello() {
    console.log("Hello");
    const url = clusterApiUrl("devnet").replace("api", "explorer-api");
    const connection = new Connection(url, "finalized");

    try {
      if (query.searchValue !== undefined) {
        pubkey = new PublicKey(query.searchValue);
        const result = (await connection.getParsedAccountInfo(pubkey)).value;

        let lamports, details;
        if (result === null) {
          lamports = 0;
        } else {
          lamports = result.lamports;

          // Only save data in memory if we can decode it
          let space: number;
          if (!("parsed" in result.data)) {
            space = result.data.length;
          } else {
            space = result.data.space;
          }

          let data: DataDetails | undefined;
          if ("parsed" in result.data) {
            data = {
              program: result.data.program,
              parsed: result.data.parsed,
            };
            details = {
              space,
              executable: result.executable,
              owner: result.owner,
              data,
            };
          } else {
            details = {
              space,
              executable: result.executable,
              owner: result.owner,
            };
          }
        }
        // data = { pubkey, lamports, details };
        setData({ pubkey, lamports, details });
        console.log("data", data);
        console.log("address", query.searchValue);
      }
    } catch (err) {}
  }

  return (
    <div className="container mt-n3">
      <div className="header">
        <div className="header-body">
          <p>{query.searchType}</p>
          <p>{query.searchValue}</p>
          <button onClick={() => Hello()}>Hello </button>
        </div>
      </div>
      {!data?.pubkey ? (
        <p>Not valid pubkey</p>
      ) : (
        <>
          <AccountHeader data={data} />
          <HistoryCard pubkey={data.pubkey} />
        </>
      )}
    </div>
  );
}
