import "./styles.css";
import {
  Supply,
  Connection,
  clusterApiUrl,
  VoteAccountStatus,
} from "@solana/web3.js";
import { useEffect, useState } from "react";
import {
  abbreviatedNumber,
  lamportsToSol,
  lamportsToSolString,
} from "./utils/index";
import React from "react";
import { Heading, VStack, Text, Box } from "@chakra-ui/react";

function displayLamports(value: number) {
  return abbreviatedNumber(lamportsToSol(value));
}

export default function SupplyCard() {
  const [supply, setSupply] = useState<Supply>();
  const [voteAccounts, setVoteAccounts] = useState<VoteAccountStatus>();
  useEffect(() => {});
  async function Hello() {
    console.log("Hello");
    const url = clusterApiUrl("devnet").replace("api", "explorer-api");
    const connection = new Connection(url, "finalized");
    const supply: Supply = (await connection.getSupply()).value;
    const voteAccounts = await connection.getVoteAccounts();
    setVoteAccounts(voteAccounts);

    console.log(supply.total);
    setSupply(supply);
  }

  const delinquentStake = React.useMemo(() => {
    if (voteAccounts) {
      return voteAccounts.delinquent.reduce(
        (prev, current) => prev + current.activatedStake,
        0
      );
    }
  }, [voteAccounts]);

  const activeStake = React.useMemo(() => {
    if (voteAccounts && delinquentStake) {
      return (
        voteAccounts.current.reduce(
          (prev, current) => prev + current.activatedStake,
          0
        ) + delinquentStake
      );
    }
  }, [voteAccounts, delinquentStake]);

  let delinquentStakePercentage;
  if (delinquentStake && activeStake) {
    delinquentStakePercentage = ((delinquentStake / activeStake) * 100).toFixed(
      1
    );
  }

  return (
    <div className="App">
      <VStack align="start" background="white" rounded="lg" p="4">
        <Heading size="md">Active Stake </Heading>
        {activeStake && supply && (
          <>
            <Text fontSize="lg" fontWeight="bold">
              <em>{displayLamports(activeStake)}</em> /{" "}
              <small>{displayLamports(supply.total)}</small>
            </Text>
            {delinquentStakePercentage && (
              <Text>
                24h Vol: Delinquent stake: <em>{delinquentStakePercentage}%</em>
              </Text>
            )}
          </>
        )}
      </VStack>
      {/* <h1>Supply </h1>
      <button onClick={() => Hello()}>Supply</button>
      {supply ? (
        <>
          <p>Total Supply: {lamportsToSolString(supply.total)}</p>
          <p>Circulating Supply: {lamportsToSolString(supply.circulating)}</p>
          <p>
            Non Circulating Supply: {lamportsToSolString(supply.nonCirculating)}
          </p>
          <div className="card-body">
            <h4>Active Stake</h4>
            {activeStake && (
              <h1>
                <em>{displayLamports(activeStake)}</em> /{" "}
                <small>{displayLamports(supply.total)}</small>
              </h1>
            )}
            {delinquentStakePercentage && (
              <h5>
                Delinquent stake: <em>{delinquentStakePercentage}%</em>
              </h5>
            )}
          </div>
        </>
      ) : (
        <p>No Data</p>
      )} */}
    </div>
  );
}
