import {
  Connection,
  clusterApiUrl,
  PublicKey,
  ParsedConfirmedTransaction,
  ConfirmedSignatureInfo
} from "@solana/web3.js";
import { useEffect, useState, useMemo } from "react";
import Moment from "react-moment";

export type History = {
  fetched: ConfirmedSignatureInfo[];
  transactionMap?: Map<any, any>;
};

const MAX_TRANSACTION_BATCH_SIZE = 10;

async function fetchParsedTransactions(
  url: string,
  transactionSignatures: string[]
) {
  const transactionMap = new Map();
  const connection = new Connection(url);

  while (transactionSignatures.length > 0) {
    const signatures = transactionSignatures.splice(
      0,
      MAX_TRANSACTION_BATCH_SIZE
    );
    const fetched = await connection.getParsedConfirmedTransactions(signatures);
    fetched.forEach(
      (parsed: ParsedConfirmedTransaction | null, index: number) => {
        if (parsed !== null) {
          transactionMap.set(signatures[index], parsed);
        }
      }
    );
  }

  return transactionMap;
}

export default function HistoryCard({ pubkey }: { pubkey: PublicKey }) {
  const [history, setHistory] = useState<History>();
  useEffect(() => {});

  // const transactionRows = useMemo(() => {
  //   if (history?.fetched) {
  //     return getTransactionRows(history.fetched);
  //   }
  //   return [];
  // }, [history]);

  async function getHistory() {
    console.log("Hello");
    const url = clusterApiUrl("devnet").replace("api", "explorer-api");
    const connection = new Connection(url);
    const options = {
      limit: 25
    };
    const fetched = await connection.getConfirmedSignaturesForAddress2(
      pubkey,
      options
    );

    let transactionMap;
    if (fetched) {
      try {
        const signatures = fetched.map((signature) => signature.signature);
        transactionMap = await fetchParsedTransactions(url, signatures);
      } catch (error) {
        console.log(error);
      }
    }
    setHistory({ fetched, transactionMap });
    console.log(fetched);
    // console.log(transactionMap);
  }

  return (
    <div className="App">
      <h1>History</h1>
      <button onClick={() => getHistory()}>get History</button>
      {history?.fetched ? (
        <>
          {history.fetched.map((item, i) => (
            <tr key={i}>
              <td>{item.signature}</td>
              <td>{item.slot}</td>

              <td>
                {item?.blockTime ? (
                  <Moment date={item.blockTime * 1000} fromNow />
                ) : (
                  ""
                )}
              </td>
              <td>{item?.err ? "Failed" : "Success"}</td>
              <td>{item?.memo ? item.memo : ""}</td>

              {/* <td>{instructionName}</td>

              <td>
                <Address pubkey={programId} link truncate truncateChars={16} />
              </td>

              <td>
                <span className={`badge badge-soft-${statusClass}`}>
                  {statusText}
                </span>
              </td> */}
            </tr>
          ))}
        </>
      ) : (
        <p> No History </p>
      )}
    </div>
  );
}
